import React, { Component } from 'react'
import {connect} from 'react-redux'
import { selectOption } from './TestAction'


class Test extends Component {

    handleOptionChange = (event) => {
        this.props.user.marksObtained = this.props.user.answer === event.target.value ? this.props.user.marksObtained + 1 : this.props.user.marksObtained
        this.props.user.selectedOPtion = event.target.value
        this.props.selectOption(this.props.user)
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
    }

    render() {
        console.log(this.props.user)
        const {school, option1, option2, option3, option4, selectedOPtion, problem, answer, maxMarks} = this.props.user
        return (
            <div class="Test">
                <h2>{school}</h2>
                <h3>{this.props.user.firstName} {this.props.user.lastName} : {this.props.user.std}</h3>
                <form onSubmit={this.handleFormSubmit}>
                    <div className="radio">
                        <label>
                            <input type="radio" value={option1} checked={selectedOPtion === option1} onChange={this.handleOptionChange.bind(this)}/>{option1}
                        </label>
                    </div>
                    <div className="radio">
                        <label>
                            <input type="radio" value={option2} checked={selectedOPtion === option2} onChange={this.handleOptionChange.bind(this)}/>{option2}
                        </label>
                    </div>
                    <div className="radio">
                        <label>
                            <input type="radio" value={option3} checked={selectedOPtion === option3} onChange={this.handleOptionChange.bind(this)}/>{option3}
                        </label>
                    </div>
                    <div className="radio">
                        <label>
                            <input type="radio" value={option4} checked={selectedOPtion === option4} onChange={this.handleOptionChange.bind(this)}/>{option4}
                        </label>
                    </div>
                </form>
                <hr></hr>
                <div>
                    <p>{problem}    Student's Answer : {selectedOPtion}     Correct Answer : {answer}    Max Marks : {maxMarks}</p>
                    <p> Marks Obtained by the student : { this.props.user.marksObtained } </p>
                </div>
            </div>
        )
    }
}


const mapStateToProps = state => {
    return{
        user : state.test
    }
}

const mapDispatchToProps = dispatch => {
    return{
        selectOption : (user) => dispatch(selectOption(user))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Test)
