import React, { Component } from 'react'
import { connect } from 'react-redux'
import { selectOption } from './TestAction'
import './Test.css'
import { Container, Card, Row, Col, Image } from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            A1: '',
            A2: '',
            A3: '',
            B1: '',
            B2: '',
            B3: '',
            C1: '',
            C2: '',
            C3: '',
            D1: '',
            D2: '',
            D3: '',
        };
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
    }

    handleResult() {
        var orderA = []
        if(this.state.A1 >= this.state.A2 && this.state.A1 >= this.state.A3){
            if(this.state.A2 >= this.state.A3){
                orderA.push(this.state.A1)
                orderA.push(this.state.A2)
                orderA.push(this.state.A3)
            }
            else {
                orderA.push(this.state.A1)
                orderA.push(this.state.A3)
                orderA.push(this.state.A2)
            }
        }
        else if(this.state.A2 >= this.state.A1 && this.state.A2 >= this.state.A3){
            if(this.state.A1 >= this.state.A3){
                orderA.push(this.state.A2)
                orderA.push(this.state.A1)
                orderA.push(this.state.A3)
            }
            else {
                orderA.push(this.state.A2)
                orderA.push(this.state.A3)
                orderA.push(this.state.A1)
            }
        }
        else if(this.state.A3 >=this.state.A1 && this.state.A3 >= this.state.A2){
            if(this.state.A1 >= this.state.A2){
                orderA.push(this.state.A3)
                orderA.push(this.state.A1)
                orderA.push(this.state.A2)
            }
            else {
                orderA.push(this.state.A3)
                orderA.push(this.state.A2)
                orderA.push(this.state.A1)
            }
        }
        console.log(orderA)
    }

    distributeCards() {
        var cardList = ['2C', '2D', '2H', '2S', '3C', '3D', '3H', '3S', '4C', '4D', '4H', '4S', '5C', '5D', '5H', '5S', '6C', '6D', '6H', '6S', '7C', '7D', '7H', '7S', '8C', '8D', '8H', '8S', '9C', '9D', '9H', '9S', '10C', '10D', '10H', '10S', 'JC', 'JD', 'JH', 'JS', 'QC', 'QD', 'QH', 'QS', 'KC', 'KD', 'KH', 'KS', 'AC', 'AD', 'AH', 'AS']

        //for seat 1
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        console.log(randomCard)
        this.setState({ A1: require('../test/cards-png/'+randomCard+'.png')})

        console.log(this.state.A1)

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ A2: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ A3: require('../test/cards-png/'+randomCard+'.png')})

        //for seat 2
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ B1: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ B2: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ B3: require('../test/cards-png/'+randomCard+'.png')})

        //for seat 3
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ C1: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ C2: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ C3: require('../test/cards-png/'+randomCard+'.png')})

        //for seat 4
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ D1: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ D2: require('../test/cards-png/'+randomCard+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ D3: require('../test/cards-png/'+randomCard+'.png')})
    }

    handleCheck() {
        this.distributeCards()
        this.setState({
            check: true
        });
    }

    handleReset() {
        this.setState({
            check: false
        });
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
    }

    render() {
        return (
            <Container style={{ alignItems: "center", background: "#777" }} fluid>
                <div className="card-board">
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.A1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.A2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.A3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}

                        </Col>
                        <Col sm={4}>
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.B1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.B2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.B3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.C1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.C2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.C3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.D1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.D2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.D3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                        <Col sm={4}>
                            <button type="button" onClick={() => this.handleCheck()}>Check</button>
                            <br />
                            <button type="button" onClick={() => this.handleResult()}>Result</button>
                            <br/>
                            <button type="button" onClick={() => this.handleReset()}>New Hand</button>
                        </Col>
                    </Row>
                </div>
            </Container >

        )
    }
}


const mapStateToProps = state => {
    return {
        user: state.test
    }
}

const mapDispatchToProps = dispatch => {
    return {
        selectOption: (user) => dispatch(selectOption(user))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Test)
