import React, { Component } from 'react'
import './CardTable.css'
import { Container, Card, Row, Col } from 'react-bootstrap'
import { getCards, getResult, addImages, clearData } from './CardTableAction';
import { connect } from 'react-redux';
import Store from '../redux/Store';
var CardBack = require('../cards-png/red_back.png');
var wallImage = require('../cards-png/playing_card.jpg');

class CardTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            showCheck: true,
            showResult: false,
            showNewHand: false
        };
        
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
        this.createFrontCardDiv = this.createFrontCardDiv.bind(this);
        this.createBackCardDiv = this.createBackCardDiv.bind(this);
    }

    componentDidMount() {
        this.distributeCards();
    }

    handleResult() {
        const cardList = Store.getState().cardTable.cards
        const names = Store.getState().main
        const nameList = []
        nameList.push(names.player1)
        nameList.push(names.player2)
        nameList.push(names.player3)
        nameList.push(names.player4)
        console.log(nameList)
        const card = {
            cards : cardList,
            names : nameList
        }
        this.setState({
            showCheck: false,
            showResult: false,
            showNewHand: true
        })
        this.props.getResult(card)
    }

    distributeCards() {
        this.props.getCards(); 
    }

    handleCheck() {
        const {cards} = this.props.cardTable
        var imageList = []
        for(var i = 0; i < cards.length; i++){
            imageList.push(require("../cards-png/" + cards[i] + ".png"))
        }
        this.props.addImages(imageList)

        this.setState({
            check: true,
            showResult: true,
            showCheck: false,
            showNewHand: false,
        })
    }

    createFrontCardDiv(images, index) {
        var divFrontCard = []
        for(var i = index; i < index+3; i++){
            divFrontCard.push(
                <Card style={{ width: '6rem', height: '9rem', border: '2px solid rgb(221, 221, 224)' }}>
                    <Card.Img src={images[i]} style={{ width: '100%', height: '100%' }} ></Card.Img>
                </Card>
            )
        }
        return divFrontCard
    }

    createBackCardDiv(){
        var divBackCard = []
        for(var i = 0; i < 3; i++){
            divBackCard.push(
                <Card style={{ width: '6rem', height: '9rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-5rem" }}>
                    <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                </Card>
            )
        }
        return divBackCard
    }

    handleReset() {
        this.distributeCards()
        this.setState({
            check: false,
            showResult: false,
            showCheck: true,
            showNewHand: false
        });
        this.props.clearData()
    }

    render() {
        const {images} = this.props.cardTable
        const { player1, player2, player3, player4} = this.props.main
        return (
            <Container style={{ backgroundImage:`url(${wallImage})`, backgroundRepeat:"no-repeat", backgroundSize: "100%", height: "740px"}}>
                <Row>
                    <Col sm={8}>
                        <Container style={{ alignItems: "center", width: "80%", borderRadius:"5px"}} fluid>
                            <div className="card-board" style={{ color: "white"}}>
                                <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                                    <Col>
                                        {!this.state.check &&( <p style={{marginLeft: "-15rem", font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player1 !== '' ? (player1): 'Seat 1' }</p> )}
                                        {this.state.check &&( <p style={{font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player1}</p> )}
                                    </Col>
                                </Row>
                                <br />
                                <Row style={{ marginLeft: "100px", textAlign: "center"}}>
                                    <Col></Col>
                                    <Col style={{ textAlign: "center" }}>
                                        {!this.state.check && (
                                            <div className="cards">
                                                {this.createBackCardDiv()}
                                            </div>
                                        )}
                                        {this.state.check && (
                                            <div className="cards">
                                                {this.createFrontCardDiv(images,0)}
                                            </div>
                                        )}

                                    </Col>
                                    <Col></Col>
                                </Row>
                                <br />
                                <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                                    <Col>
                                    {!this.state.check &&( <p style={{marginLeft: "-15rem", font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player2 !== '' ? (player2): 'Seat 2' }</p> )}
                                    {this.state.check &&( <p style={{font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player2}</p> )}
                                    </Col>
                                    <Col>
                                    </Col>
                                    <Col>
                                    {!this.state.check &&( <p style={{marginLeft: "-15rem", font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player3 !== '' ? (player3): 'Seat 3' }</p> )}
                                    {this.state.check &&( <p style={{font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player3}</p> )}
                                    </Col>
                                </Row>
                                <br />
                                <Row style={{ marginLeft: "100px" , textAlign: "center" }}>
                                    <Col style={{ textAlign: "center" }}>
                                        {!this.state.check && (
                                            <div className="cards">
                                                {this.createBackCardDiv()}
                                            </div>
                                        )}
                                        {this.state.check && (
                                            <div className="cards">
                                                {this.createFrontCardDiv(images,3)}
                                            </div>
                                        )}
                                    </Col>
                                    <Col>
                                    </Col>
                                    <Col style={{ textAlign: "center" }}>
                                        {!this.state.check && (
                                            <div className="cards">
                                                {this.createBackCardDiv()}
                                            </div>
                                        )}
                                        {this.state.check && (
                                            <div className="cards">
                                                {this.createFrontCardDiv(images,6)}
                                            </div>
                                        )}
                                    </Col>
                                </Row>
                                <br />
                                <Row style={{ marginLeft: "100px" , textAlign: "center" }}>
                                    <Col>
                                    </Col>
                                    <Col style={{ textAlign: "center" }}>
                                        {!this.state.check && (
                                            <div className="cards">
                                                {this.createBackCardDiv()}
                                            </div>
                                        )}
                                        {this.state.check && (
                                            <div className="cards">
                                                {this.createFrontCardDiv(images,9)}
                                            </div>
                                        )}
                                    </Col>
                                    <Col>
                                    </Col>
                                </Row>
                                <br/>
                                <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                                    <Col>
                                    {!this.state.check &&( <p style={{marginLeft: "-15rem", font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player4 !== '' ? (player4): 'Seat 4' }</p> )}
                                    {this.state.check &&( <p style={{font: "20px Arial, sans-serif", fontWeight: "bold"}}>{player4}</p> )}
                                    </Col>
                                </Row>
                            </div>
                        </Container >
                    </Col>
                    <Col sm={4}>
                        <Container>
                            <Row>
                                <Col>
                                    <button type="button" className="btn" onClick={() => this.handleCheck()} disabled={!this.state.showCheck}>Check</button>
                                </Col>
                            </Row>
                            <Row>
                                <Col style={{ marginTop: "5px"}}>
                                    <button type="button" className="btn1" onClick={() => this.handleResult()} disabled={!this.state.showResult}>Result</button>
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <button type="button" className="btn1" onClick={() => this.handleReset()} disabled={!this.state.showNewHand}>New Hand</button>
                                </Col>
                            </Row>
                        </Container>
                    </Col>
                </Row>
            </Container>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        cardTable : state.cardTable,
        main : state.main
    }
}

const mapDispatchToProps = (dispatch) => {
    return{
        getCards: () => dispatch(getCards()),
        getResult: (cardList) => dispatch(getResult(cardList)),
        addImages: (imageList) => dispatch(addImages(imageList)),
        clearData: () => dispatch(clearData())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CardTable)
