import React, { Component } from 'react'
import './Test.css'
import { Container, Card, Row, Col } from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            imageA1: '',
            imageA2: '',
            imageA3: '',
            imageB1: '',
            imageB2: '',
            imageB3: '',
            imageC1: '',
            imageC2: '',
            imageC3: '',
            imageD1: '',
            imageD2: '',
            imageD3: '',
            cardA1: '',
            cardA2: '',
            cardA3: '',
            cardB1: '',
            cardB2: '',
            cardB3: '',
            cardC1: '',
            cardC2: '',
            cardC3: '',
            cardD1: '',
            cardD2: '',
            cardD3: '',
            handRank: [],
            winner: [],
        };
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
    }

    handleResult() {
        var orderA = this.arrangeOrder("A")
        var orderB = this.arrangeOrder("B")
        var orderC = this.arrangeOrder("C")
        var orderD = this.arrangeOrder("D")
        this.giveHandRanking(orderA)
        this.giveHandRanking(orderB)
        this.giveHandRanking(orderC)
        this.giveHandRanking(orderD)
        var sortedRank = this.state.handRank.sort((a, b) => b - a)
        // var winArr = null
        if (sortedRank[0] === 1) {
            var max = 2
            if (orderA[0] >= max) {
                max = orderA[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('A')
                })
            }
            if (orderB[0] >= max) {
                max = orderB[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('B')
                })
            }
            if (orderC[0] >= max) {
                max = orderC[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            if (orderD[0] >= max) {
                max = orderD[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
        }
        else if (sortedRank[0] === 2) {
            var max = 4
            if (orderA[0] >= max) {
                max = orderA[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('A')
                })
            }
            if (orderB[0] > max) {
                max = orderB[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('B')
                })
            }
            else if (orderB[0] === max) {
                this.setState({
                    winner: this.state.winner.concat('B')
                })
            }
            if (orderC[0] > max) {
                max = orderC[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            else if (orderC[0] === max) {
                this.setState({
                    winner: this.state.winner.concat('C')
                })
            }
            if (orderD[0] >= max) {
                max = orderD[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
            else if (orderD[0] === max) {
                this.setState({
                    winner: this.state.winner.concat('D')
                })
            }
        }
        else if (sortedRank[0] === 3) {
            var max = 2
            //last index of the order
            var maxL = orderA[2]
            if (orderA[0] >= max) {
                max = orderA[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('A')
                })
            }
            if (orderB[0] > max) {
                max = orderB[0]
                maxL = orderB[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('A')
                })
            }
            else if (orderB[0] === max && orderB[2] === maxL) {
                this.setState({
                    winner: this.state.winner.concat('B')
                })
            }
            else if (orderB[0] === max && orderB[2] > maxL) {
                maxL = orderB[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('B')
                })
            }
            if (orderC[0] > max) {
                max = orderC[0]
                maxL = orderC[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            else if (orderC[0] === max && orderC[2] === maxL) {
                this.setState({
                    winner: this.state.winner.concat('C')
                })
            }
            else if (orderC[0] === max && orderC[2] > maxL) {
                maxL = orderC[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            if (orderD[0] > max) {
                max = orderD[0]
                maxL = orderD[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
            else if (orderD[0] === max && orderD[2] === maxL) {
                this.setState({
                    winner: this.state.winner.concat('D')
                })
            }
            else if (orderD[0] === max && orderD[2] > maxL) {
                maxL = orderD[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
        }
        else if (sortedRank[0] === 4) {
            var max = 2
            var maxM = orderA[1]
            var maxL = orderA[2]
            if (orderA[0] >= max) {
                max = orderA[0]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('A')
                })
            }
            if (orderB[0] > max) {
                max = orderB[0]
                maxM = orderB[1]
                maxL = orderB[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('B')
                })
            }
            else if (orderB[0] === max && orderB[1] > maxM) {
                max = orderB[0]
                maxM = orderB[1]
                maxL = orderB[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('B')
                })
            }
            else if (orderB[0] === max && orderB[1] === maxM && orderB[2] > maxL) {
                max = orderB[0]
                maxM = orderB[1]
                maxL = orderB[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('B')
                })
            }
            else if (orderB[0] === max && orderB[1] === maxM && orderB[2] === maxL) {
                this.setState({
                    winner: this.state.winner.concat('B')
                })
            }
            if (orderC[0] > max) {
                max = orderC[0]
                maxM = orderC[1]
                maxL = orderC[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            else if (orderC[0] === max && orderC[1] > maxM) {
                max = orderC[0]
                maxM = orderC[1]
                maxL = orderC[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            else if (orderC[0] === max && orderC[1] === maxM && orderC[2] > maxL) {
                max = orderC[0]
                maxM = orderC[1]
                maxL = orderC[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('C')
                })
            }
            else if (orderC[0] === max && orderC[1] === maxM && orderC[2] === maxL) {
                this.setState({
                    winner: this.state.winner.concat('C')
                })
            }
            if (orderD[0] > max) {
                max = orderD[0]
                maxM = orderD[1]
                maxL = orderD[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
            else if (orderD[0] === max && orderD[1] > maxM) {
                max = orderD[0]
                maxM = orderD[1]
                maxL = orderD[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
            else if (orderD[0] === max && orderD[1] === maxM && orderD[2] > maxL) {
                max = orderD[0]
                maxM = orderD[1]
                maxL = orderD[2]
                this.setState({
                    winner: [],
                    winner: this.state.winner.concat('D')
                })
            }
            else if (orderD[0] === max && orderD[1] === maxM && orderD[2] === maxL) {
                this.setState({
                    winner: this.state.winner.concat('D')
                })
            }
        }
        console.log(this.state.winner)
    }

    // giving ranking for all hands( all-same: 1, sequence: 2, pair: 3, highest-card: 4)
    giveHandRanking(order) {
        if (order[0] === order[1] === order[2]) {
            this.setState({
                handRank: this.state.handRank.concat(1)
            })
        }
        else if (order[0] - 1 === order[1] && order[1] - 1 === order[2]) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if ((order[0] === order[1]) || (order[0] === order[2]) || (order[1] === order[2])) {
            this.setState({
                handRank: this.state.handRank.concat(3)
            })
        }
        else {
            this.setState({
                handRank: this.state.handRank.concat(4)
            })
        }
    }

    arrangeOrder(char) {
        var order = []
        var card1 = char === 'A' ? this.state.cardA1 : char === 'B' ? this.state.cardB1 : char === 'C' ? this.state.cardC1 : this.state.cardD1
        var card2 = char === 'A' ? this.state.cardA2 : char === 'B' ? this.state.cardB2 : char === 'C' ? this.state.cardC2 : this.state.cardD2
        var card3 = char === 'A' ? this.state.cardA3 : char === 'B' ? this.state.cardB3 : char === 'C' ? this.state.cardC3 : this.state.cardD3
        order.push(parseInt(card1), parseInt(card2), parseInt(card3))
        order.sort((a, b) => b - a)
        if ((order[0] !== order[1]) && (order[1] === order[2])) {
            var card = order.splice(0, 1)[0]
            order.splice(2, 0, card)
        }
        return order;
    }

    getCardValue(card) {
        let cardMap = new Map([['A', 14], ['K', 13], ['Q', 12], ['J', 11], ['10', 10], ['9', 9], ['8', 8], ['7', 7], ['6', 6], ['5', 5], ['4', 4], ['3', 3], ['2', 2]]);
        var cardValue = 0;
        if (!card.includes('10')) {
            cardValue = cardMap.get(card[0])
        }
        else {
            cardValue = cardMap.get(card.substring(0, 2))
        }
        return cardValue;
    }

    distributeCards() {
        var imageSourceList = ['2C', '2D', '2H', '2S', '3C', '3D', '3H', '3S', '4C', '4D', '4H', '4S', '5C', '5D', '5H', '5S', '6C', '6D', '6H', '6S', '7C', '7D', '7H', '7S', '8C', '8D', '8H', '8S', '9C', '9D', '9H', '9S', '10C', '10D', '10H', '10S', 'JC', 'JD', 'JH', 'JS', 'QC', 'QD', 'QH', 'QS', 'KC', 'KD', 'KH', 'KS', 'AC', 'AD', 'AH', 'AS']

        //for seat A
        var randomIndex = Math.floor(Math.random() * imageSourceList.length);
        var randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageA1: require('../test/cards-png/' + randomCard + '.png'), cardA1: this.getCardValue(randomCard) })

        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageA2: require('../test/cards-png/' + randomCard + '.png'), cardA2: this.getCardValue(randomCard) })

        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageA3: require('../test/cards-png/' + randomCard + '.png'), cardA3: this.getCardValue(randomCard) })

        //for seat B
        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageB1: require('../test/cards-png/' + randomCard + '.png'), cardB1: this.getCardValue(randomCard) })

        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageB2: require('../test/cards-png/' + randomCard + '.png'), cardB2: this.getCardValue(randomCard) })

        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageB3: require('../test/cards-png/' + randomCard + '.png'), cardB3: this.getCardValue(randomCard) })

        //for seat C
        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageC1: require('../test/cards-png/' + randomCard + '.png'), cardC1: this.getCardValue(randomCard) })

        randomIndex = Math.floor(Math.random() * imageSourceList.length);
        randomCard = imageSourceList.splice(randomIndex, 1)[0];
        this.setState({ imageC2: require('../test/cards-png/' + randomCard + '.png'), cardC2: this.getCardValue(randomCard) })

