import {SELECT_OPTION} from './TestTypes'
import { TestModel } from '../model/TestModel'

const initState = new TestModel(initialState);
const initialState = {
    id : 1,
    firstName : 'Rihen',
    lastName : 'Bajaj',
    school : 'Indian Institute of technology',
    std : 'Sem-I',
    qId : 1,
    problem : '2 + 3 :',
    option1 : '2',
    option2 : '3',
    option3 : '4',
    option4 : '5',
    selectedOPtion : '2',
    answer : '5',
    marksObtained : 0,
    maxMarks : 10
}

const TestReducer = (state = initState, action) => {
    switch(action.type){
        case SELECT_OPTION : return {
            ...state,
            test : action.payload 
        }
        default : return state
    }
}

export default TestReducer
