import React from 'react';

class HomeComponent extends React.Component{

	constructor(){
		super();
		this.state = {
			order:{
				name:"paneer masala",
				quantity: 0
			}	
		};
	}

	addOrder(){
		this.setState({
			order:{
				quantity: this.state.order.quantity + 1
			}
		});
	}

	render(){
		return(
			<div className="paneer-masala">
				<h2>Paneer Masala</h2>
				<div className="paneer-masala-card">
					<button onClick={()=>{this.addOrder()}}></button>
				</div>
				<div>
					<p>Ordered:{this.state.order.quantity}</p>
				</div>
			</div>
		);
	}
}

export default HomeComponent;
