import React from 'react';
import './HomeComponent.css';

class HomeComponent extends React.Component{

	constructor(){
		super();
		this.state = {
			order:{
				name:"paneer masala",
				quantity: 0
			}	
		};
	}

	addOrder(){
		this.setState({
			order:{
				quantity: this.state.order.quantity + 1
			}
		});
	}

	render(){
		return(
			<div>
				<div className="biryani-card">
					<h2>Biryani</h2>
				</div>	
				<div className="biryani-menu">
					<div className="item">
						<p>Hyderabadi Chichen Dum Biryani</p>
					</div>
					<span className="a">
					<p>&#x20b9; 120</p></span>
					<span className="b">
					<button onClick={()=>{this.addOrder()}}>Add</button></span>
				</div>
			</div>
		);
	}
}

export default HomeComponent;
