import React from 'react';
import './HomeComponent.css';

class HomeComponent extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			user:props.user,
			quantity:0,
			order:{
				name:"paneer masala",
				quantity: 0
			}	
		};
	}

	addOrder(){
		this.setState({
			quantity:this.state.quantity+1
		});
	}

	render(){
		return(
			<div>
				<div className="user-header"><h1>Welcome {this.state.user.name}, Order here</h1></div>
				<div className="menu">
				<div className="biryani-card">
					<h2>Biryani</h2>
				</div>	
				<div className="biryani-menu">
					<div className="item">
						<p>Hyderabadi Chicken Dum Biryani</p>
					</div>
					<span className="a">
					<p>&#x20b9; 150</p></span>
					{this.state.quantity===0 ? <button className="add-button" onClick={()=>{this.addOrder()}}>Add</button> : <div className="after-add"><p className="minus">-</p><p className="quantity-btn">{this.state.quantity}</p><p className="plus">+</p></div>}
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Veg Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 90</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Mushroom Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 110</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Cashewnut Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 210</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Paneer Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 120</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				</div>
				<div className="order">
					<h2>Your Order</h2>
					<div className="cart">
					</div>
				</div>
			</div>
		);
	}
}

export default HomeComponent;
