import React, { Component } from 'react'
import './Test.css'
import { Container, Card, Row, Col } from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            imageA1: '',
            imageA2: '',
            imageA3: '',
            imageB1: '',
            imageB2: '',
            imageB3: '',
            imageC1: '',
            imageC2: '',
            imageC3: '',
            imageD1: '',
            imageD2: '',
            imageD3: '',
            cardA1: '',
            cardA2: '',
            cardA3: '',
            cardB1: '',
            cardB2: '',
            cardB3: '',
            cardC1: '',
            cardC2: '',
            cardC3: '',
            cardD1: '',
            cardD2: '',
            cardD3: '',
            handRank: [],
            winner: [],
        };
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
    }

    handleResult() {
        var map = {
            A: 14,
            K: 13,
            Q: 12,
            J: 11
        }
        var orderA = this.arrangeOrder("A")
        var orderB = this.arrangeOrder("B")
        var orderC = this.arrangeOrder("C")
        var orderD = this.arrangeOrder("D")
        this.giveHandRanking(this.arrangeOrder("A"))
        this.giveHandRanking(this.arrangeOrder("B"))
        this.giveHandRanking(this.arrangeOrder("C"))
        this.giveHandRanking(this.arrangeOrder("D"))
        var min = this.state.handRank[0]
        var winArr = null
        if (this.state.handRank[0] === this.state.handRank[1] === this.state.handRank[2] === this.state.handRank[3]) {
            if(this.state.handRank[0] === 1){
                var max = 2
                for(var i in map){
                    if(!orderA[0].includes(10) && orderA[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('A')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('A')
                        })
                    }
                    if(!orderB[0].includes(10) && orderB[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('B')
                        })
                    }
                    else if(orderB[0].includes(10) && orderB[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('B')
                        })
                    }
                    if(!orderC[0].includes(10) && orderC[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('C')
                        })
                    }
                    else if(orderC[0].includes(10) && orderC[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('C')
                        })
                    }
                    if(!orderD[0].includes(10) && orderD[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('D')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('D')
                        })
                    }
                }
            }
            else if(this.state.handRank[0] === 2){
                var max = 4
                for(var i in map){
                    if(!orderA[0].includes(10) && orderA[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('A')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('A')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('A')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('A')
                        })
                    }
                    if(!orderB[0].includes(10) && orderB[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('B')
                        })
                    }
                    else if(!orderB[0].includes(10) && orderB[0][0] === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('B')
                        })
                    }
                    else if(orderB[0].includes(10) && orderB[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('B')
                        })
                    }
                    else if(orderB[0].includes(10) && orderB[0].substring(0,2) === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('B')
                        })
                    }
                    if(!orderC[0].includes(10) && orderC[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('C')
                        })
                    }
                    else if(!orderC[0].includes(10) && orderC[0][0] === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('C')
                        })
                    }
                    else if(orderC[0].includes(10) && orderC[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('C')
                        })
                    }
                    else if(orderC[0].includes(10) && orderC[0].substring(0,2) === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('C')
                        })
                    }
                    if(!orderD[0].includes(10) && orderD[0][0] === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('D')
                        })
                    }
                    else if(!orderD[0].includes(10) && orderD[0][0] === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('D')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max < map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : [],
                            winner : this.state.winner.concat('D')
                        })
                    }
                    else if(orderA[0].includes(10) && orderA[0].substring(0,2) === i.toString() && max === map.get(i)){
                        max = map.get(i)
                        this.setState({
                            winner : this.state.winner.concat('D')
                        })
                    }
                }
            }
        }
        else {
            for (var i = 0; i < this.state.handRank.length; i++) {
                if (this.state.handRank[i] < min) {
                    min = this.state.handRank[i]
                    winArr = []
                    winArr.push(i)
                }
                else if (this.state.handRank[i] === min) {
                    winArr.push(i)
                }
            }
        }
    }

    // giving ranking for all hands( all-same: 1, sequence: 2, pair: 3, highest-card: 4)
    giveHandRanking(order) {
        if (order[0] === order[1] === order[2]) {
            this.setState({
                handRank: this.state.handRank.concat(1)
            })
        }
        else if (order[0].includes('A') && order[0].includes('K') && order[0].includes('Q')) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if (order[0].includes('K') && order[0].includes('Q') && order[0].includes('J')) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if (order[0].includes('Q') && order[0].includes('J') && order[0].includes('10')) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if (order[0].includes('J') && order[0].includes('10') && order[0].includes('9')) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if (order[0].includes('10') && order[0].includes('9') && order[0].includes('8')) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if (order[0][0] < 10 && order[1][0] === order[0][0] - 1 && order[2][0] === order[0][0] - 2) {
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if (order[0] === order[1] || order[0] === order[2] || order[2] === order[1]) {
            this.setState({
                handRank: this.state.handRank.concat(3)
            })
        }
        else {
            this.setState({
                handRank: this.state.handRank.concat(4)
            })
        }
    }

    arrangeOrder(char) {
        var order = []
        var card1 = char === 'A' ? this.state.cardA1 : char === 'B' ? this.state.cardB1 : char === 'C' ? this.state.cardC1 : this.state.cardD1
        var card2 = char === 'A' ? this.state.cardA2 : char === 'B' ? this.state.cardB2 : char === 'C' ? this.state.cardC2 : this.state.cardD2
        var card3 = char === 'A' ? this.state.cardA3 : char === 'B' ? this.state.cardB3 : char === 'C' ? this.state.cardC3 : this.state.cardD3
        if (card1 >= card2 && card1 >= card3) {
            if (card2 >= card3) {
                order.push(card1)
                order.push(card2)
                order.push(card3)
            }
            else {
                order.push(card1)
                order.push(card3)
                order.push(card2)
            }
        }
        else if (card2 >= card1 && card2 >= card3) {
            if (card1 >= card3) {
                order.push(card2)
                order.push(card1)
                order.push(card3)
            }
            else {
                order.push(card2)
                order.push(card3)
                order.push(card1)
            }
        }
        else if (card3 >= card1 && card3 >= card2) {
            if (card1 >= card2) {
                order.push(card3)
                order.push(card1)
                order.push(card2)
            }
            else {
                order.push(card3)
                order.push(card2)
                order.push(card1)
            }
        }

        if (order[1][0] === 'A' && order[0][0] !== 'A') {
            order.splice(0, 0, order[1])
            order.splice(2, 1)
        }
        if (order[2][0] === 'A') {
            if (order[0][0] !== 'A') {
                order.splice(0, 0, order[2])
                order.splice(3, 1)
            }
            else if (order[1][0] !== 'A') {
                order.splice(1, 0, order[2])
                order.splice(3, 1)
            }
        }
        if (order[1][0] === 'K' && order[0][0] !== 'A' && order[0][0] !== 'K') {
            order.splice(0, 0, order[1])
            order.splice(2, 1)
        }
        if (order[2][0] === 'K') {
            if (order[0][0] !== 'A' && order[0][0] !== 'K') {
                order.splice(0, 0, order[2])
                order.splice(3, 1)
            }
            else if (order[1][0] !== 'A' && order[1][0] !== 'K') {
                order.splice(1, 0, order[2])
                order.splice(3, 1)
            }
        }
        if (order[1].includes("10") && order[0][0] < 10) {
            order.splice(0, 0, order[1])
            order.splice(2, 1)
        }
        if (order[2].includes("10")) {
            if (order[0][0] < 10) {
                order.splice(0, 0, order[2])
                order.splice(3, 1)
            }
            else if (order[1][0] < 10) {
