import React from 'react';
import './App.css';
import LoginContainer from './component/loginComponent/LoginContainer';
import SignUpContainer from './component/SignupContainer';
import HomeComponent from './component/userComponent/HomeComponent';
import './mainlogo.jpg';

class App extends React.Component {

	constructor(props){
		super(props);
		this.state={
			login:true,
			signup:false,
			isUserValid:false,
			isCreated:false,
			user:{
				id: null,
				userName: "rihen",
				name: null,
				mob: null,
				email: null,
				registrationDate: null,
				blocked: null,
				userPassword: null
			}
		};
	}

	handleLoginClick(userloggedIn){
		this.setState({
			isUserValid:true,
			user:userloggedIn
		});
		console.log(this.state.user);
	}

	handleSignupClick(){
		this.setState({
			isCreated:true,
			login:true,
			signup:false
		});
	}

	render(){
		if(this.state.isUserValid){
			return(
				<HomeComponent user={this.state.user}/>
			);
		}else{
			return (
				<div className="main-container">
					<div className="buttons">
						{this.state.login || this.state.isCreated ? <p className="colorlogin" onClick={()=>{this.setState({login:true,signup:false});}}>Login</p> : <p className="login" onClick={()=>{this.setState({login:true,signup:false});}}>Login</p>}
						{this.state.signup ? <p className="colorsignup" onClick={()=>{this.setState({login:false,signup:true});}}>Signup</p> : <p className="signup" onClick={()=>{this.setState({login:false,signup:true});}}>Signup</p>}
					</div>
					{this.state.login?<LoginContainer user={this.user} onClick={this.handleLoginClick.bind(this)}/>:null}
					{this.state.signup?<SignUpContainer onClick={this.handleSignupClick.bind(this)}/>:null}
					{this.state.isUserValid?<p>{this.state.user.userName}</p>:null}
				</div>
			);
		}

	}

}

export default App;
