import React, { Component } from 'react'
import './CardTable.css'
import { Container, Card, Row, Col } from 'react-bootstrap'
import { getCards, getResult } from './CardTableAction';
import { connect } from 'react-redux';
var CardBack = require('../card-table/cards-png/red_back.png');


class CardTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            images: [],
            cardA1: '',
            cardA2: '',
            cardA3: '',
            cardB1: '',
            cardB2: '',
            cardB3: '',
            cardC1: '',
            cardC2: '',
            cardC3: '',
            cardD1: '',
            cardD2: '',
            cardD3: '',
            handRank: [],
            winner: [],
            winnerHand: [],
            player1: 'Clarke',
            player2: 'Murphy',
            player3: 'Bellamy',
            player4: 'Raven',
            showCheck: true,
            showResult: false,
            showNewHand: false
        };
        
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
    }

    componentDidMount() {
        this.distributeCards();
    }

    componentDidUpdate() {
        
    }

    handleResult() {
        this.setState({
            showCheck: false,
            showResult: false,
            showNewHand: true
        })
        var orderA = this.arrangeOrder("A")
        var orderB = this.arrangeOrder("B")
        var orderC = this.arrangeOrder("C")
        var orderD = this.arrangeOrder("D")
        var handRanking = []
        handRanking.push(this.giveHandRanking(orderA))
        handRanking.push(this.giveHandRanking(orderB))
        handRanking.push(this.giveHandRanking(orderC))
        handRanking.push(this.giveHandRanking(orderD))
        var sortedRank = [...handRanking]
        sortedRank.sort((a, b) => a - b)

        var winArr = []
        var winHand = []
        if (sortedRank[0] === 1) {
            var max = 2
            if (parseInt(orderA[0]) >= max && handRanking[0] === 1) {
                max = parseInt(orderA[0])
                winArr = []
                winArr = winArr.concat("A")
                winHand = []
                winHand = winHand.concat(orderA)
            }
            if (parseInt(orderB[0]) >= max && handRanking[1] === 1) {
                max = parseInt(orderB[0])
                winArr = []
                winArr = winArr.concat("B")
                winHand = []
                winHand = winHand.concat(orderB)
            }
            if (parseInt(orderC[0]) >= max && handRanking[2] === 1) {
                max = parseInt(orderC[0])
                winArr = []
                winArr = winArr.concat("C")
                winHand = []
                winHand = winHand.concat(orderC)
            }
            if (parseInt(orderD[0]) >= max && handRanking[3] === 1) {
                max = parseInt(orderD[0])
                winArr = []
                winArr = winArr.concat("D")
                winHand = []
                winHand = winHand.concat(orderD)
            }
        }
        else if (sortedRank[0] === 2) {

            var max = 4
            if (parseInt(orderA[0]) >= max && handRanking[0] === 2) {
                max = parseInt(orderA[0])
                winArr = []
                winArr = winArr.concat("A")
                winHand = []
                winHand = winHand.concat(orderA)
            }

            if (handRanking[1] === 2) {
                if (parseInt(orderB[0]) > max) {
                    max = parseInt(orderB[0])
                    winArr = []
                    winArr = winArr.concat("B")
                    winHand = []
                    winHand = winHand.concat(orderB)
                }
                else if (parseInt(orderB[0]) === max) {
                    winArr = winArr.concat("B")
                    winHand = winHand.concat(orderB)
                }
            }

            if (handRanking[2] === 2) {
                if (parseInt(orderC[0]) > max) {
                    max = parseInt(orderC[0])
                    winArr = []
                    winArr = winArr.concat("C")
                    winHand = []
                    winHand = winHand.concat(orderC)
                }
                else if (parseInt(orderC[0]) === max) {
                    winArr = winArr.concat("C")
                    winHand = winHand.concat(orderC)
                }
            }

            if (handRanking[3] === 2) {
                if (parseInt(orderD[0]) >= max) {
                    max = parseInt(orderD[0])
                    winArr = []
                    winArr = winArr.concat("D")
                    winHand = []
                winHand = winHand.concat(orderD)
                }
                else if (parseInt(orderD[0]) === max) {
                    winArr = winArr.concat("D")
                winHand = winHand.concat(orderD)
                }
            }
        }
        else if (sortedRank[0] === 3) {
            var max = 2
            //last index of the order
            var maxL = parseInt(orderA[2])
            if (handRanking[0] === 3) {
                if (parseInt(orderA[0]) >= max) {
                    max = parseInt(orderA[0])
                    winArr = []
                    winArr = winArr.concat("A")
                    winHand = []
                winHand = winHand.concat(orderA)
                }
            }

            if (handRanking[1] === 3) {
                if (parseInt(orderB[0]) > max) {
                    max = parseInt(orderB[0])
                    maxL = parseInt(orderB[2])
                    winArr = []
                    winArr = winArr.concat("B")
                    winHand = []
                winHand = winHand.concat(orderB)
                }
                else if (parseInt(orderB[0]) === max && parseInt(orderB[2]) === maxL) {
                    winArr = winArr.concat("B")
                winHand = winHand.concat(orderB)
                }
                else if (parseInt(orderB[0]) === max && parseInt(orderB[2]) > maxL) {
                    maxL = parseInt(orderB[2])
                    winArr = []
                    winArr = winArr.concat("B")
                    winHand = []
                winHand = winHand.concat(orderB)
                }
            }

            if (handRanking[2] === 3) {
                if (parseInt(orderC[0]) > max) {
                    max = parseInt(orderC[0])
                    maxL = parseInt(orderC[2])
                    winArr = []
                    winArr = winArr.concat("C")
                    winHand = []
                winHand = winHand.concat(orderC)
                }
                else if (parseInt(orderC[0]) === max && parseInt(orderC[2]) === maxL) {
                    winArr = winArr.concat("C")
                winHand = winHand.concat(orderC)
                }
                else if (parseInt(orderC[0]) === max && parseInt(orderC[2]) > maxL) {
                    maxL = parseInt(orderC[2])
                    winArr = []
                    winArr = winArr.concat("C")
                    winHand = []
                winHand = winHand.concat(orderC)
                }
            }

            if (handRanking[3] === 3) {
                if (parseInt(orderD[0]) > max) {
                    max = parseInt(orderD[0])
                    maxL = parseInt(orderD[2])
                    winArr = []
                    winArr = winArr.concat("D")
                    winHand = []
                winHand = winHand.concat(orderD)
                }
                else if (parseInt(orderD[0]) === max && parseInt(orderD[2]) === maxL) {
                    winArr = winArr.concat("D")
                    winHand = winHand.concat(orderA)
                }
                else if (parseInt(orderD[0]) === max && parseInt(orderD[2]) > maxL) {
                    maxL = parseInt(orderD[2])
                    winArr = []
                    winArr = winArr.concat("D")
                    winHand = []
                winHand = winHand.concat(orderD)
                }
            }
        }
        else if (sortedRank[0] === 4) {

            var max = 2
            var maxM = parseInt(orderA[1])
            var maxL = parseInt(orderA[2])
            if (parseInt(orderA[0]) >= max && handRanking[0] === 4) {
                max = parseInt(orderA[0])
                winArr = []
                winArr = winArr.concat("A")
                winHand = []
                winHand = winHand.concat(orderA)
            }

            if (handRanking[1] === 4) {
                if (parseInt(orderB[0]) > max) {
                    max = parseInt(orderB[0])
                    maxM = parseInt(orderB[1])
                    maxL = parseInt(orderB[2])
                    winArr = []
                    winArr = winArr.concat("B")
                    winHand = []
                winHand = winHand.concat(orderB)
                }
                else if (parseInt(orderB[0]) === max && parseInt(orderB[1]) > maxM) {
                    max = parseInt(orderB[0])
                    maxM = parseInt(orderB[1])
                    maxL = parseInt(orderB[2])
                    winArr = []
                    winArr = winArr.concat("B")
                    winHand = []
                winHand = winHand.concat(orderB)
                }
                else if (parseInt(orderB[0]) === max && parseInt(orderB[1]) === maxM && parseInt(orderB[2]) > maxL) {
                    max = parseInt(orderB[0])
                    maxM = parseInt(orderB[1])
                    maxL = parseInt(orderB[2])
                    winArr = []
                    winArr = winArr.concat("B")
                    winHand = []
                winHand = winHand.concat(orderB)
                }
                else if (parseInt(orderB[0]) === max && parseInt(parseInt(orderB[1])) === maxM && parseInt(orderB[2]) === maxL) {
                    winArr = winArr.concat("B")
                winHand = winHand.concat(orderB)
                }
            }

            if (handRanking[2] === 4) {
                if (parseInt(orderC[0]) > max) {
                    max = parseInt(orderC[0])
                    maxM = parseInt(orderC[1])
                    maxL = parseInt(orderC[2])
                    winArr = []
                    winArr = winArr.concat("C")
                    winHand = []
                winHand = winHand.concat(orderC)
                }
                else if (parseInt(orderC[0]) === max && parseInt(orderC[1]) > maxM) {
                    max = parseInt(orderC[0])
                    maxM = parseInt(orderC[1])
                    maxL = parseInt(orderC[2])
                    winArr = []
                    winArr = winArr.concat("C")
                    winHand = []
                winHand = winHand.concat(orderC)
                }
                else if (parseInt(orderC[0]) === max && parseInt(orderC[1]) === maxM && parseInt(orderC[2]) > maxL) {
                    max = parseInt(orderC[0])
                    maxM = parseInt(orderC[1])
                    maxL = parseInt(orderC[2])
                    winArr = []
                    winArr = winArr.concat("C")
                    winHand = []
                winHand = winHand.concat(orderC)
                }
                else if (parseInt(orderC[0]) === max && parseInt(orderC[1]) === maxM && parseInt(orderC[2]) === maxL) {
                    winArr = winArr.concat("C")
                winHand = winHand.concat(orderC)
                }
            }

            if (handRanking[3] === 4) {
                if (parseInt(orderD[0]) > max) {
                    max = parseInt(orderD[0])
                    maxM = parseInt(orderD[1])
                    maxL = parseInt(orderD[2])
                    winArr = []
                    winArr = winArr.concat("D")
                    winHand = []
                winHand = winHand.concat(orderD)
                }
                else if (parseInt(orderD[0]) === max && parseInt(orderD[1]) > maxM) {
                    max = parseInt(orderD[0])
                    maxM = parseInt(orderD[1])
                    maxL = parseInt(orderD[2])
                    winArr = []
                    winArr = winArr.concat("D")
                    winHand = []
                    winHand = winHand.concat(orderD)
                }
                else if (parseInt(orderD[0]) === max && parseInt(orderD[1]) === maxM && parseInt(orderD[2]) > maxL) {
                    max = parseInt(orderD[0])
                    maxM = parseInt(orderD[1])
                    maxL = parseInt(orderD[2])
                    winArr = []
                    winArr = winArr.concat("D")
                    winHand = []
                    winHand = winHand.concat(orderD)
                }
                else if (parseInt(orderD[0]) === max && parseInt(orderD[1]) === maxM && parseInt(orderD[2]) === maxL) {
                    winArr = winArr.concat("D")
                    winHand = winHand.concat(orderD)
                }
            }
        }
        this.setState({
            winner: winArr, 
            winnerHand: winHand 
        });
    }

    // giving ranking for all hands( all-same: 1, sequence: 2, pair: 3, highest-card: 4)
    giveHandRanking(order) {
        if ((order[0] === order[1]) && (order[0] === order[2])) {
            return 1
        }
        else if (order[0] - 1 === order[1] && order[1] - 1 === order[2]) {
            return 2
        }
        else if ((order[0] === order[1]) || (order[0] === order[2]) || (order[1] === order[2])) {
            return 3
        }
        else {
            return 4
        }
    }

    arrangeOrder(char) {
        var order = []
        var card1 = char === 'A' ? this.state.cardA1 : char === 'B' ? this.state.cardB1 : char === 'C' ? this.state.cardC1 : this.state.cardD1
        var card2 = char === 'A' ? this.state.cardA2 : char === 'B' ? this.state.cardB2 : char === 'C' ? this.state.cardC2 : this.state.cardD2
        var card3 = char === 'A' ? this.state.cardA3 : char === 'B' ? this.state.cardB3 : char === 'C' ? this.state.cardC3 : this.state.cardD3
        order.push(parseInt(card1), parseInt(card2), parseInt(card3))
        order.sort((a, b) => b - a)
        if ((order[0] !== order[1]) && (order[1] === order[2])) {
            var card = order.splice(0, 1)[0]
            order.splice(2, 0, card)
        }
        return order;
    }

    getCardValue(card) {
        let cardMap = new Map([['A', 14], ['K', 13], ['Q', 12], ['J', 11], ['10', 10], ['9', 9], ['8', 8], ['7', 7], ['6', 6], ['5', 5], ['4', 4], ['3', 3], ['2', 2], ['1', 1]]);
        var cardValue = 0;
        if (!card.includes('10')) {
            cardValue = cardMap.get(card[0])
        }
        else {
            cardValue = cardMap.get(card.substring(0, 2))
        }
        return cardValue;
    }

    //changing the Ace value based on perference with highest possibility. eg (A,2,3 => Ace: 1 => 1,2,3) (otherwise => Ace: 14)
    checkAcePreference() {

        //seat 1
        if (this.state.cardA1 === 14 && ((this.state.cardA2 === 2 && this.state.cardA3 === 3) || (this.state.cardA2 === 3 && this.state.cardA3 === 2))) {
            this.setState({
                cardA1: 1
            })
        }
