import React, { Component } from 'react'
import { connect } from 'react-redux'
import { selectOption } from './TestAction'
import './Test.css'
import { Container, Card, Row, Col, Image } from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            A1: '',
            A2: '',
            A3: '',
            B1: '',
            B2: '',
            B3: '',
            C1: '',
            C2: '',
            C3: '',
            D1: '',
            D2: '',
            D3: '',
        };
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    distributeCards() {
        var cardList = ['2C', '2D', '2H', '2S', '3C', '3D', '3H', '3S', '4C', '4D', '4H', '4S', '5C', '5D', '5H', '5S', '6C', '6D', '6H', '6S', '7C', '7D', '7H', '7S', '8C', '8D', '8H', '8S', '9C', '9D', '9H', '9S', '10C', '10D', '10H', '10S', 'JC', 'JD', 'JH', 'JS', 'QC', 'QD', 'QH', 'QS', 'KC', 'KD', 'KH', 'KS', 'AC', 'AD', 'AH', 'AS']

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomItem = cardList.splice(randomIndex, 1)[0];
    }

    handleCheck() {
        this.setState({
            check: true
        });
    }

    handleReset() {
        this.setState({
            check: false
        });
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
    }

    render() {
        return (
            <Container style={{ alignItems: "center", background: "#777" }} fluid>
                <div className="card-board">
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        {/* <Card.Img src={A1} style={{ width: '100%', height: '100%' }} ></Card.Img> */}
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        {/* <Card.Img src={A2} style={{ width: '100%', height: '100%' }}></Card.Img> */}
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        {/* <Card.Img src={A3} style={{ width: '100%', height: '100%' }}></Card.Img> */}
                                    </Card>
                                </div>
                            )}

                        </Col>
                        <Col sm={4}>
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
