                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        {/* <Card.Img src={C1} style={{ width: '100%', height: '100%' }} ></Card.Img> */}
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
