import React from 'react';
import './LoginContainer.css';

class LoginContainer extends React.Component{

	constructor(props){
		super(props);
		this.state={
			user:{
				id: null,
				userName: null,
				name: null,
				mob: null,
				email: null,
				registrationDate: null,
				blocked: null,
				userPassword: null
			}
		};
	}

	login(){
		var uname= document.getElementById("t1").value;
		var pwd= document.getElementById("t2").value;
		var letters = /^[A-Za-z]+$/;

		if(uname===''){
			alert('Please enter the user name.');
		}
		else if(pwd===''){
			alert('Please enter password.');
		}
		else if(letters.test(uname)){
			fetch('http://localhost:8080/api/user/username?username='+uname, {
			method: 'GET',
			headers: {'Content-Type': 'application/json'}
			})
			.then(response => response.json())
			.then(response =>{
				console.log(response);
				this.setState({user:{
					id:response.id,
					userName:response.userName,
					name:response.name,
					mob:response.mob,
					email:response.email,
					registrationDate:response.registrationDate,
					blocked:response.blocked,
					userPassword:response.userPassword
				}});
				console.log(this.state.user.userName);

				if(this.state.user.userName !== uname){
					alert('Please enter valid user name.');
				}
				else if(this.state.user.userPassword!==pwd){
					alert('Please enter valid password.');
				}
				else if((this.state.user.userName === uname) && (this.state.user.userPassword === pwd)){
					alert('Successfully Logged In');
				}
			})
			.catch(error => console.log('An error occured ', error))
		}
	}

	render(){
			return(
				<div className="container">
					<div className="login">
						<table cellSpacing="2" align="center" cellPadding="8" border="0">
						<tbody>
						<tr>
							<td align="right">Enter Username :</td>
							<td><input type="text" placeholder="Enter Username here" id="t1" className="tb" /></td>
						</tr>
						<tr>
							<td align="right">Enter Password :</td>
							<td><input type="password" placeholder="Enter Password here" id="t2" className="tb" /></td>
						</tr>
						<tr>
						<td></td>
						<td>
							<input type="submit" value="Login" className="btn" onClick={this.props.onClick()} /></td>
						</tr>
						</tbody>
						</table>
					</div>
				</div>
			);
		
	}
}

export default LoginContainer;
