import React, { Component } from 'react'
import './Test.css'
import { Container, Card, Row, Col } from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            imageA1: '',
            imageA2: '',
            imageA3: '',
            imageB1: '',
            imageB2: '',
            imageB3: '',
            imageC1: '',
            imageC2: '',
            imageC3: '',
            imageD1: '',
            imageD2: '',
            imageD3: '',
            cardA1: '',
            cardA2: '',
            cardA3: '',
            cardB1: '',
            cardB2: '',
            cardB3: '',
            cardC1: '',
            cardC2: '',
            cardC3: '',
            cardD1: '',
            cardD2: '',
            cardD3: '',
            handRank: [],
            winner: [],
            winnerHand: [],
            player1: 'Clarke',
            player2: 'Murphy',
            player3: 'Bellamy',
            player4: 'Raven',
            showCheck: true,
            showResult: false,
            showNewHand: false
        };
        
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
    }

    componentDidMount() {
        this.distributeCards();
      }

    handleResult() {
        this.setState({
            showCheck: false,
            showResult: false,
            showNewHand: true
        })
        var orderA = this.arrangeOrder("A")
        var orderB = this.arrangeOrder("B")
        var orderC = this.arrangeOrder("C")
        var orderD = this.arrangeOrder("D")
        var handRanking = []
        handRanking.push(this.giveHandRanking(orderA))
        handRanking.push(this.giveHandRanking(orderB))
        handRanking.push(this.giveHandRanking(orderC))
        handRanking.push(this.giveHandRanking(orderD))
        console.log(handRanking)
        var sortedRank = [...handRanking]
        sortedRank.sort((a, b) => a - b)

        var winArr = []
        var winHand = []
        if (sortedRank[0] === 1) {
            console.log(1)
            console.log(sortedRank)
            var max = 2
            if (parseInt(orderA[0]) >= max && handRanking[0] === 1) {
                console.log('handRanking' + 0)
                max = parseInt(orderA[0])
                winArr = []
                winArr = winArr.concat("A")
                winHand = []
                winHand = winHand.concat(orderA)
            }
            if (parseInt(orderB[0]) >= max && handRanking[1] === 1) {
                console.log('handRanking' + 1)
                max = parseInt(orderB[0])
                winArr = []
                winArr = winArr.concat("B")
                winHand = []
                winHand = winHand.concat(orderB)
            }
            if (parseInt(orderC[0]) >= max && handRanking[2] === 1) {
                console.log('handRanking' + 2)
                max = parseInt(orderC[0])
                winArr = []
                winArr = winArr.concat("C")
                winHand = []
                winHand = winHand.concat(orderC)
            }
            if (parseInt(orderD[0]) >= max && handRanking[3] === 1) {
                console.log('handRanking' + 3)
                max = parseInt(orderD[0])
                winArr = []
                winArr = winArr.concat("D")
                winHand = []
                winHand = winHand.concat(orderD)
            }
        }
        else if (sortedRank[0] === 2) {
            console.log(2)
            console.log(sortedRank)

            var max = 4
