    // giving ranking for all hands( all-same: 1, sequence: 2, pair: 3, highest-card: 4)
    giveHandRanking(order) {
        if ((order[0] === order[1]) && (order[0] === order[2])) {
            return 1
        }
        else if (order[0] - 1 === order[1] && order[1] - 1 === order[2]) {
            return 2
        }
        else if ((order[0] === order[1]) || (order[0] === order[2]) || (order[1] === order[2])) {
            return 3
        }
        else {
            return 4
        }
    }

    arrangeOrder(char) {
        var order = []
        var card1 = char === 'A' ? this.state.cardA1 : char === 'B' ? this.state.cardB1 : char === 'C' ? this.state.cardC1 : this.state.cardD1
        var card2 = char === 'A' ? this.state.cardA2 : char === 'B' ? this.state.cardB2 : char === 'C' ? this.state.cardC2 : this.state.cardD2
        var card3 = char === 'A' ? this.state.cardA3 : char === 'B' ? this.state.cardB3 : char === 'C' ? this.state.cardC3 : this.state.cardD3
        order.push(parseInt(card1), parseInt(card2), parseInt(card3))
        order.sort((a, b) => b - a)
        if ((order[0] !== order[1]) && (order[1] === order[2])) {
            var card = order.splice(0, 1)[0]
            order.splice(2, 0, card)
        }
        return order;
    }

    getCardValue(card) {
        let cardMap = new Map([['A', 14], ['K', 13], ['Q', 12], ['J', 11], ['10', 10], ['9', 9], ['8', 8], ['7', 7], ['6', 6], ['5', 5], ['4', 4], ['3', 3], ['2', 2], ['1', 1]]);
        var cardValue = 0;
        if (!card.includes('10')) {
            cardValue = cardMap.get(card[0])
        }
        else {
            cardValue = cardMap.get(card.substring(0, 2))
        }
        return cardValue;
    }

