                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageC1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageC2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageC3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" , textAlign: "center" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageD1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageD2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageD3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                        <Col sm={4} style={{textAlign: "right"}}>
                            <button type="button" className="btn" onClick={() => this.handleCheck()} disabled={!this.state.showCheck}>Check</button>
                            <br />
                            <button type="button" className="btn1" onClick={() => this.handleResult()} disabled={!this.state.showResult}>Result</button>
                            <br />
                            <button type="button" className="btn1" onClick={() => this.handleReset()} disabled={!this.state.showNewHand}>New Hand</button>
                        </Col>
                    </Row>
                    <br/>
                    <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                        <Col sm={12}>
                        {!this.state.check &&( <p style={{marginLeft: "-20rem"}}>{this.state.player4}'s Seat</p> )}
                        {this.state.check &&( <p>{this.state.player4}'s Seat</p> )}
                        </Col>
                    </Row>
                </div>
            </Container >

        )
    }
}

export default Test
