import React, { Component } from 'react'
import { connect } from 'react-redux'
import { selectOption } from './TestAction'
import './Test.css'
import { Container, Card, Row, Col, Image } from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            A1: '',
            A2: '',
            A3: '',
            B1: '',
            B2: '',
            B3: '',
            C1: '',
            C2: '',
            C3: '',
            D1: '',
            D2: '',
            D3: '',
        };
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    distributeCards() {
        var cardList = ['2C', '2D', '2H', '2S', '3C', '3D', '3H', '3S', '4C', '4D', '4H', '4S', '5C', '5D', '5H', '5S', '6C', '6D', '6H', '6S', '7C', '7D', '7H', '7S', '8C', '8D', '8H', '8S', '9C', '9D', '9H', '9S', '10C', '10D', '10H', '10S', 'JC', 'JD', 'JH', 'JS', 'QC', 'QD', 'QH', 'QS', 'KC', 'KD', 'KH', 'KS', 'AC', 'AD', 'AH', 'AS']

        //for seat 1
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ A1: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ A2: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ A3: require('../test/cards-png/'+{randomCard}+'.png')})

        //for seat 2
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ B1: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ B2: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ B3: require('../test/cards-png/'+{randomCard}+'.png')})

        //for seat 3
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ C1: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ C2: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ C3: require('../test/cards-png/'+{randomCard}+'.png')})

        //for seat 4
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ D1: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ D2: require('../test/cards-png/'+{randomCard}+'.png')})

        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ D3: require('../test/cards-png/'+{randomCard}+'.png')})
    }

    handleCheck() {
        this.setState({
            check: true
        });
    }

    handleReset() {
        this.setState({
            check: false
        });
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
    }

    render() {
