import React from 'react';
import './App.css';
import LoginContainer from './component/loginComponent/LoginContainer';
import SignUpContainer from './component/SignupContainer';

class App extends React.Component {

	constructor(){
		super();
		this.state={
			login:true,
			signup:false
		};
	}

	render(){
		return (
			<div className="main-container">
				<div className="buttons">
					<p className="login" onClick={()=>{this.setState({login:true,signup:false});}}>Login</p>
					<p className="login">Signup</p>
				</div>
				{this.state.login?<LoginContainer/>:null}
				{this.state.signup?<SignUpContainer/>:null}
			</div>
		);
	}

}

export default App;
