                cardB1: 1
            })
        }
        else if (this.state.cardB2 === 14 && ((this.state.cardB1 === 2 && this.state.cardB3 === 3) || (this.state.cardB1 === 3 && this.state.cardB3 === 2))) {
            this.setState({
                cardB2: 1
            })
        }
        else if (this.state.cardB3 === 14 && ((this.state.cardB1 === 2 && this.state.cardB2 === 3) || (this.state.cardB1 === 3 && this.state.cardB2 === 2))) {
            this.setState({
                cardB3: 1
            })
        }

        //seat 3
        if (this.state.cardC1 === 14 && ((this.state.cardC2 === 2 && this.state.cardC3 === 3) || (this.state.cardC2 === 3 && this.state.cardC3 === 2))) {
            this.setState({
                cardC1: 1
            })
        }
        else if (this.state.cardC2 === 14 && ((this.state.cardC1 === 2 && this.state.cardC3 === 3) || (this.state.cardC1 === 3 && this.state.cardC3 === 2))) {
            this.setState({
                cardC2: 1
            })
        }
        else if (this.state.cardC3 === 14 && ((this.state.cardC1 === 2 && this.state.cardC2 === 3) || (this.state.cardC1 === 3 && this.state.cardC2 === 2))) {
            this.setState({
