            })
        }

        //seat 4
        if (this.state.cardD1 === 14 && ((this.state.cardD2 === 2 && this.state.cardD3 === 3) || (this.state.cardD2 === 3 && this.state.cardD3 === 2))) {
            this.setState({
                cardD1: 1
            })
        }
        else if (this.state.cardD2 === 14 && ((this.state.cardD1 === 2 && this.state.cardD3 === 3) || (this.state.cardD1 === 3 && this.state.cardD3 === 2))) {
            this.setState({
                cardD2: 1
            })
        }
        else if (this.state.cardD3 === 14 && ((this.state.cardD1 === 2 && this.state.cardD2 === 3) || (this.state.cardD1 === 3 && this.state.cardD2 === 2))) {
            this.setState({
                cardD3: 1
            })
        }
    }

    distributeCards() {
        this.props.getCards(); 
    }

    handleCheck() {
        const {cards} = this.props.cardTable
        var imageList = []
        for(var i = 0; i < cards.length; i++){
            imageList.push(require("../cards-png/" + cards[i] + ".png"))
        }
        this.props.addImages(imageList)

        this.setState({
            check: true,
            showResult: true,
            showCheck: false,
            showNewHand: false,
        })
    }

    createFrontCardDiv(images, index) {
        var divFrontCard = []
        for(var i = index; i < index+3; i++){
            divFrontCard.push(
                <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                    <Card.Img src={images[i]} style={{ width: '100%', height: '100%' }} ></Card.Img>
                </Card>
            )
        }
        return divFrontCard
    }

    createBackCardDiv(){
        var divBackCard = []
        for(var i = 0; i < 3; i++){
            divBackCard.push(
                <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                    <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                </Card>
            )
        }
        return divBackCard
    }

    handleReset() {
        this.distributeCards()
        this.setState({
            check: false,
            showResult: false,
            showCheck: true,
            showNewHand: false
        });
    }

    render() {
        const {images, cards, winners} = this.props.cardTable
        return (
            <Container style={{ alignItems: "center", background: "#777" }} fluid>
                <div className="card-board">
                    <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                        <Col sm={12}>
                        {!this.state.check &&( <p style={{marginLeft: "-20rem"}}>{this.state.player1}'s Seat</p> )}
                        {this.state.check &&( <p>{this.state.player1}'s Seat</p> )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px", textAlign: "center"}}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    {this.createBackCardDiv()}
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    {this.createFrontCardDiv(images,0)}
                                </div>
                            )}

                        </Col>
                        <Col sm={4}>
                            {winners.length > 0 && (
                                <div>
                                <h3>{winners[0].id === 1 ? this.state.player1 : winners[0].id === "B" ? this.state.player2 : winners[0].id === "C" ? this.state.player3 : this.state.player4 } &nbsp; Wins</h3>
                                <p>with hand : {winners[0].hand[0]} {winners[0].hand[1]} {winners[0].hand[2]}</p>
                                </div>

                            )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                        <Col sm={4}>
                        {!this.state.check &&( <p style={{marginLeft: "-20rem"}}>{this.state.player2}'s Seat</p> )}
                        {this.state.check &&( <p>{this.state.player2}'s Seat</p> )}
                        </Col>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4}>
                        {!this.state.check &&( <p style={{marginLeft: "-20rem"}}>{this.state.player3}'s Seat</p> )}
                        {this.state.check &&( <p>{this.state.player3}'s Seat</p> )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" , textAlign: "center" }}>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    {this.createBackCardDiv()}
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    {this.createFrontCardDiv(images,3)}
                                </div>
                            )}
                        </Col>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    {this.createBackCardDiv()}
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    {this.createFrontCardDiv(images,6)}
                                </div>
                            )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" , textAlign: "center" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    {this.createBackCardDiv()}
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    {this.createFrontCardDiv(images,9)}
                                </div>
                            )}
                        </Col>
                        <Col sm={4} style={{textAlign: "right"}}>
                            <button type="button" className="btn" onClick={() => this.handleCheck()} disabled={!this.state.showCheck}>Check</button>
                            <br />
                            <button type="button" className="btn1" onClick={() => this.handleResult()} disabled={!this.state.showResult}>Result</button>
                            <br />
                            <button type="button" className="btn1" onClick={() => this.handleReset()} disabled={!this.state.showNewHand}>New Hand</button>
                        </Col>
                    </Row>
                    <br/>
                    <Row style={{ marginLeft: "100px", textAlign: "center" }}>
                        <Col sm={12}>
                        {!this.state.check &&( <p style={{marginLeft: "-20rem"}}>{this.state.player4}'s Seat</p> )}
                        {this.state.check &&( <p>{this.state.player4}'s Seat</p> )}
                        </Col>
                    </Row>
                </div>
            </Container >

        )
    }
}

const mapStateToProps = (state) => {
    return{
        cardTable : state.cardTable
    }
}

const mapDispatchToProps = (dispatch) => {
    return{
        getCards: () => dispatch(getCards()),
        getResult: (cardList) => dispatch(getResult(cardList)),
        addImages: (imageList) => dispatch(addImages(imageList))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CardTable)
