import Input from './signupComponent/Input';
import Button from './signupComponent/Button';

class SignUp extends React.Component{

	constructor(props){
		super(props);

		this.state = {
      		newUser: {
        	name: '',
        	email: '',
       		username: '',
       		password: '',
        	confirmpassword: ''
		  }
		} 
		  this.handleFormSubmit = this.handleFormSubmit.bind(this);
    	  this.handleClearForm = this.handleClearForm.bind(this);
	}

	handleFormSubmit() {
    // Form submission logic
	  }
	  
  	handleClearForm() {
    // Logic for resetting the form
  	}

	render() {
    	return (
      		<form className="container" onSubmit={this.handleFormSubmit}>

        	<Input type={'text'} title= {'Full Name'} name= {'name'} value={this.state.newUser.name} placeholder = {'Enter your name'} handleChange = {this.handleFullName} /> {/* Name of the user */}
        	<Input type={'text'} title= {'Email'} name= {'email'} value={this.state.newUser.email} placeholder = {'Enter your email'} handleChange = {this.handleFullName} /> {/* Input for Age */} 
        	<Input type={'text'} title= {'User Name'} name= {'username'} value={this.state.newUser.username} placeholder = {'Enter your username'} handleChange = {this.handleFullName} /> {/* Name of the user */}
        	<Input type={'text'} title= {'Password'} name= {'password'} value={this.state.newUser.password} placeholder = {'Password'} handleChange = {this.handleFullName} /> {/* Input for Age */} 
        	<Input type={'text'} title= {'Confirm Password'} name= {'confirmpassword'} value={this.state.newUser.confirmpassword} placeholder = {'Confirm Password'} handleChange = {this.handleFullName} /> {/* Name of the user */}
        	<Button /> {/* Clear the form */}
      		</form>
    	);
  	}
	
}

export default SignUp;
