	render() {
    	return (
      		<form className="container" onSubmit={this.handleFormSubmit}>

        	<Input type={'text'} title= {'Full Name'} name= {'name'} value={this.state.newUser.name} placeholder = {'Enter your name'} handleChange = {this.handleFullName} /> {/* Name of the user */}
        	<Input type={'text'} title= {'Full Name'} name= {'name'} value={this.state.newUser.name} placeholder = {'Enter your name'} handleChange = {this.handleFullName} /> {/* Input for Age */} 
        	<Input type={'text'} title= {'Full Name'} name= {'name'} value={this.state.newUser.name} placeholder = {'Enter your name'} handleChange = {this.handleFullName} /> {/* Name of the user */}
        	<Input type={'text'} title= {'Full Name'} name= {'name'} value={this.state.newUser.name} placeholder = {'Enter your name'} handleChange = {this.handleFullName} /> {/* Input for Age */} 
        	<Input type={'text'} title= {'Full Name'} name= {'name'} value={this.state.newUser.name} placeholder = {'Enter your name'} handleChange = {this.handleFullName} /> {/* Name of the user */}
        	<Button /> {/* Clear the form */}
      		</form>
    	);
  	}
	
}

export default SignUp;
