import React from 'react';
import './HomeComponent.css';

class HomeComponent extends React.Component{

	constructor(){
		super();
		this.state = {
			order:{
				name:"paneer masala",
				quantity: 0
			}	
		};
	}

	addOrder(){
		this.setState({
			order:{
				quantity: this.state.order.quantity + 1
			}
		});
	}

	render(){
		return(
			<div>
				<div className="biryani-card">
					<h2>Biryani</h2>
				</div>	
				<div className="biryani-menu">
					<div className="item">
						<p>Hyderabadi Chicken Dum Biryani</p>
					</div>
					<span className="a">
					<p>&#x20b9; 150</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Veg Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 90</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Mushroom Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 110</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Cashewnut Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 210</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
				<div className="biryani-menu">
					<div className="item">
						<p>Paneer Biryani</p>
					</div>
					<span className="a">
					<p id="price">&#x20b9; 120</p></span>
					<button id="add-button" onClick={()=>{this.addOrder()}}>Add</button>
				</div>
			</div>
		);
	}
}

export default HomeComponent;
