import React from 'react'
import _ from 'underscore';

class SignUp extends React.Component{

	saveAndContinue() {

		var canProceed = this.validateEmail(this.state.email) && !_.isEmpty(this.state.statesValue) && this.refs.password.isValid() && this.refs.passwordConfirm.isValid();
		
		if(canProceed){
			var data = {
        	email: this.state.email,
        	state: this.state.statesValue
     		}
      		alert('Thanks.');
		} else {
      		this.refs.email.isValid();
     		this.refs.state.isValid();
      		this.refs.companyName.isValid();
      		this.refs.password.isValid();
      		this.refs.passwordConfirm.isValid();
    	}
	}

	validateEmail(event) {
    var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(event);
  	}


	render() {
		return(
		<div className="create_account_screen">

        <div className="create_account_form">
          <h1>Create account</h1>
          <p>Example of form validation built with React.</p>
          <form onSubmit={this.saveAndContinue}>

		  </form>
        </div>

	  </div>
	  );
	}
}

export default SignUp;
