import React from 'react';
import './mainpage.css';
import HomeComponent from './component/userComponent/HomeComponent';

class MainPage extends React.Component{
	constructor(){
		super();
		this.state={
			isLoggedIn:false
		};
	}

	register(){
		this.setState({
			isLoggedIn:true
		});
	}

	render(){
		if(this.state.isLoggedIn){
			return <HomeComponent/>
		}
		else{
			return (
    		<div className="main-page">
				<div className="main-container">
				<div className="create-header">
					<h2>Create Your Account</h2>
				</div>
				{/*sign up here*/}
				<div className="sign-up">
					<table cellSpacing="2" align="center" cellPadding="8" border="0">
					<tbody>
					<tr>
					<td align="right">Enter Name :</td>
					<td><input type="text" placeholder="Enter user here" id="t1" className="tb" /></td>
					</tr>
					<tr>
					<td align="right">Enter Email ID :</td>
					<td><input type="text" placeholder="Enter Email ID here" id="t2" className="tb" /></td>
					</tr>
					<tr>
					<td align="right">Enter Contact :</td>
					<td><input type="text" placeholder="Enter mobile here" id="t3" className="tb" /></td>
					</tr>
					<tr>
					<td align="right">Enter Username :</td>
					<td><input type="text" placeholder="Enter Username here" id="t4" className="tb" /></td>
					</tr>
					<tr>
					<td align="right">Enter Password :</td>
					<td><input type="password" placeholder="Enter Password here" id="t5" className="tb" /></td>
					</tr>
					<tr>
					<td align="right">Enter Confirm Password :</td>
					<td><input type="password" placeholder="Enter Password here" id="t6" className="tb" /></td>
					</tr>
					<tr>
					<td></td>
					<td>
					<input type="submit" value="Create Account" className="btn" onClick={()=>this.register()} /></td>
					</tr>
					</tbody>
					</table>
				</div>	
			</div>	
			</div>
  			);
		}
  		
	}
}

export default MainPage;
