import React, { Component } from 'react'
import { connect } from 'react-redux'
import { selectOption } from './TestAction'
import './Test.css'
import { Container, Card } from 'react-bootstrap'

class Test extends Component {

    handleOptionChange = (event) => {
        event.preventDefault();
        this.props.user.marksObtained = this.props.user.answer === event.target.value ? this.props.user.marksObtained + 1 : this.props.user.marksObtained
        this.props.user.selectedOPtion = event.target.value
        this.props.selectOption(this.props.user)
    }

    handleFormSubmit = (event) => {
        event.preventDefault();
    }

    render() {
        console.log(this.props.user)
        const { school, option1, option2, option3, option4, selectedOPtion, problem, answer, maxMarks } = this.props.user
        return (
            <Container>
                <Row>
                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Body>
                                <Card.Title>Card Title</Card.Title>
                                <Card.Text>
                                    Some quick example text to build on the card title and make up the bulk of
                                    the card's content.
                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        )
    }
}


const mapStateToProps = state => {
    return {
        user: state.test
    }
}

const mapDispatchToProps = dispatch => {
    return {
        selectOption: (user) => dispatch(selectOption(user))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Test)
