import React from 'react';
import './App.css';
import LoginContainer from './component/loginComponent/LoginContainer';
import SignUpContainer from './component/SignupContainer';

class App extends React.Component {

	constructor(){
		super();
		this.state={
			login:true,
			signup:false,
		};
		var isUserValid = false;
	}


	handleLoginClick(){
		this.isUserValid = true;
	}

	render(){
		return (
			<div className="main-container">
				<div className="buttons">
					<p className="login" onClick={()=>{this.setState({login:true,signup:false});}}>Login</p>
					<p className="login" onClick={()=>{this.setState({login:false,signup:true});}}>Signup</p>
				</div>
				{this.state.login?<LoginContainer onClick={this.handleLoginClick}/>:null}
				{this.state.signup?<SignUpContainer/>:null}
				{this.state.isUserValid?<p>valid user</p>:null}
			</div>
		);
	}

}

export default App;
