import React, { Component } from 'react'
import './Test.css'
import { Container, Card, Row, Col} from 'react-bootstrap'
var CardBack = require('../test/cards-png/red_back.png');


class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            check: false,
            imageA1: '',
            imageA2: '',
            imageA3: '',
            imageB1: '',
            imageB2: '',
            imageB3: '',
            imageC1: '',
            imageC2: '',
            imageC3: '',
            imageD1: '',
            imageD2: '',
            imageD3: '',
            cardA1: '',
            cardA2: '',
            cardA3: '',
            cardB1: '',
            cardB2: '',
            cardB3: '',
            cardC1: '',
            cardC2: '',
            cardC3: '',
            cardD1: '',
            cardD2: '',
            cardD3: '',
            handRank: []
        };
        this.handleCheck = this.handleCheck.bind(this);
        this.handleReset = this.handleReset.bind(this);
        this.handleResult = this.handleResult.bind(this);
    }

    handleResult() {
        this.giveHandRanking(this.arrangeOrder("A"))
        this.giveHandRanking(this.arrangeOrder("B"))
        this.giveHandRanking(this.arrangeOrder("C"))
        this.giveHandRanking(this.arrangeOrder("D"))
    }

    // giving ranking for all hands( all-same: 1, sequence: 2, pair: 3, highest-card: 4)
    giveHandRanking(order) {
        if(order[0] === order[1] === order[2]){
            this.setState({
                handRank: this.state.handRank.concat(1)
            })
        }
        else if(order[0].includes('A') && order[0].includes('K') && order[0].includes('Q')){
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if(order[0].includes('K') && order[0].includes('Q') && order[0].includes('J')){
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if(order[0].includes('Q') && order[0].includes('J') && order[0].includes('10')){
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if(order[0].includes('J') && order[0].includes('10') && order[0].includes('9')){
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if(order[0].includes('10') && order[0].includes('9') && order[0].includes('8')){
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if(order[0][0] < 10 && order[1][0] === order[0][0]-1 && order[2][0] === order[0][0]-2){
            this.setState({
                handRank: this.state.handRank.concat(2)
            })
        }
        else if(order[0] === order[1] || order[0] === order[2] || order[2] === order[1]){
            this.setState({
                handRank: this.state.handRank.concat(3)
            })
        }
        else{
            this.setState({
                handRank: this.state.handRank.concat(4)
            })
        }
    }

    arrangeOrder(char) {
        var order = []
        var card1 = char === 'A' ? this.state.cardA1 : char === 'B' ? this.state.cardB1 : char === 'C' ? this.state.cardC1 : this.state.cardD1
        var card2 = char === 'A' ? this.state.cardA2 : char === 'B' ? this.state.cardB2 : char === 'C' ? this.state.cardC2 : this.state.cardD2
        var card3 = char === 'A' ? this.state.cardA3 : char === 'B' ? this.state.cardB3 : char === 'C' ? this.state.cardC3 : this.state.cardD3
        if (card1 >= card2 && card1 >= card3) {
            if (card2 >= card3) {
                order.push(card1)
                order.push(card2)
                order.push(card3)
            }
            else {
                order.push(card1)
                order.push(card3)
                order.push(card2)
            }
        }
        else if (card2 >= card1 && card2 >= card3) {
            if (card1 >= card3) {
                order.push(card2)
                order.push(card1)
                order.push(card3)
            }
            else {
                order.push(card2)
                order.push(card3)
                order.push(card1)
            }
        }
        else if (card3 >= card1 && card3 >= card2) {
            if (card1 >= card2) {
                order.push(card3)
                order.push(card1)
                order.push(card2)
            }
            else {
                order.push(card3)
                order.push(card2)
                order.push(card1)
            }
        }

        if (order[1][0] === 'A' && order[0][0] !== 'A') {
            order.splice(0, 0, order[1])
            order.splice(2, 1)
        }
        if (order[2][0] === 'A') {
            if (order[0][0] !== 'A') {
                order.splice(0, 0, order[2])
                order.splice(3, 1)
            }
            else if (order[1][0] !== 'A') {
                order.splice(1, 0, order[2])
                order.splice(3, 1)
            }
        }
        if (order[1][0] === 'K' && order[0][0] !== 'A' && order[0][0] !== 'K') {
            order.splice(0, 0, order[1])
            order.splice(2, 1)
        }
        if (order[2][0] === 'K') {
            if (order[0][0] !== 'A' && order[0][0] !== 'K') {
                order.splice(0, 0, order[2])
                order.splice(3, 1)
            }
            else if (order[1][0] !== 'A' && order[1][0] !== 'K') {
                order.splice(1, 0, order[2])
                order.splice(3, 1)
            }
        }
        if (order[1].includes("10") && order[0][0] < 10) {
            order.splice(0, 0, order[1])
            order.splice(2, 1)
        }
        if (order[2].includes("10")) {
            if (order[0][0] < 10) {
                order.splice(0, 0, order[2])
                order.splice(3, 1)
            }
            else if (order[1][0] < 10) {
                order.splice(1, 0, order[2])
                order.splice(3, 1)
            }
        }
        console.log(order)
        return order;
    }

    distributeCards() {
        var cardList = ['2C', '2D', '2H', '2S', '3C', '3D', '3H', '3S', '4C', '4D', '4H', '4S', '5C', '5D', '5H', '5S', '6C', '6D', '6H', '6S', '7C', '7D', '7H', '7S', '8C', '8D', '8H', '8S', '9C', '9D', '9H', '9S', '10C', '10D', '10H', '10S', 'JC', 'JD', 'JH', 'JS', 'QC', 'QD', 'QH', 'QS', 'KC', 'KD', 'KH', 'KS', 'AC', 'AD', 'AH', 'AS']

        //for seat 1
        var randomIndex = Math.floor(Math.random() * cardList.length);
        var randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageA1: require('../test/cards-png/' + randomCard + '.png'), cardA1: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageA2: require('../test/cards-png/' + randomCard + '.png'), cardA2: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageA3: require('../test/cards-png/' + randomCard + '.png'), cardA3: randomCard })

        //for seat 2
        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageB1: require('../test/cards-png/' + randomCard + '.png'), cardB1: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageB2: require('../test/cards-png/' + randomCard + '.png'), cardB2: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageB3: require('../test/cards-png/' + randomCard + '.png'), cardB3: randomCard })

        //for seat 3
        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageC1: require('../test/cards-png/' + randomCard + '.png'), cardC1: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageC2: require('../test/cards-png/' + randomCard + '.png'), cardC2: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageC3: require('../test/cards-png/' + randomCard + '.png'), cardC3: randomCard })

        //for seat 4
        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageD1: require('../test/cards-png/' + randomCard + '.png'), cardD1: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageD2: require('../test/cards-png/' + randomCard + '.png'), cardD2: randomCard })

        randomIndex = Math.floor(Math.random() * cardList.length);
        randomCard = cardList.splice(randomIndex, 1)[0];
        this.setState({ imageD3: require('../test/cards-png/' + randomCard + '.png'), cardD3: randomCard })
    }

    handleCheck() {
        this.distributeCards()
        this.setState({
            check: true
        });
    }

    handleReset() {
        this.setState({
            check: false
        });
    }

    render() {
        return (
            <Container style={{ alignItems: "center", background: "#777" }} fluid>
                <div className="card-board">
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageA1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageA2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageA3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}

                        </Col>
                        <Col sm={4}>
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageB1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageB2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageB3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageC1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageC2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageC3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                    </Row>
                    <br />
                    <Row style={{ marginLeft: "100px" }}>
                        <Col sm={4}>
                        </Col>
                        <Col sm={4} style={{ textAlign: "center" }}>
                            {!this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)', marginLeft: "-6rem" }}>
                                        <Card.Img src={CardBack} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                            {this.state.check && (
                                <div className="cards">
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageD1} style={{ width: '100%', height: '100%' }} ></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageD2} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                    <Card style={{ width: '8rem', height: '12rem', border: '2px solid rgb(221, 221, 224)' }}>
                                        <Card.Img src={this.state.imageD3} style={{ width: '100%', height: '100%' }}></Card.Img>
                                    </Card>
                                </div>
                            )}
                        </Col>
                        <Col sm={4}>
                            <button type="button" onClick={() => this.handleCheck()}>Check</button>
                            <br />
                            <button type="button" onClick={() => this.handleResult()}>Result</button>
                            <br />
                            <button type="button" onClick={() => this.handleReset()}>New Hand</button>
                        </Col>
                    </Row>
                </div>
            </Container >

        )
    }
}

export default Test
