import React from 'react';
import './App.css';
import LoginContainer from './component/loginComponent/LoginContainer';
import SignUpContainer from './component/SignupContainer';
import HomeComponent from './component/userComponent/HomeComponent';

class App extends React.Component {

	constructor(){
		super();
		this.state={
			login:true,
			signup:false,
			isValidUser:false
		};
	}

	render(){
		if(this.state.isUserValid){
			return(
				<HomeComponent user={this.state.user}/>
			);
		}
		return (
			<div className="main-container">
				<div className="buttons">
					<p className="login" onClick={()=>{this.setState({login:true,signup:false});}}>Login</p>
					<p className="login" onClick={()=>{this.setState({login:false,signup:true});}}>Signup</p>
				</div>
				{this.state.login?<LoginContainer isValidUser={this.state.isValidUser}/>:null}
				{this.state.signup?<SignUpContainer/>:null}
			</div>
		);
	}

}

export default App;
