import React from 'react';
import './LoginContainer.css';
import HomeComponent from '../userComponent/HomeComponent';

class LoginContainer extends React.Component{

	constructor(){
		super();
		this.state={
			isUserValid:false,
			user:{
				id: null,
				userName: null,
				name: null,
				mob: null,
				email: null,
				registrationDate: null,
				blocked: false,
				userPassword: null
			}
		};
	}

	login(){
		var uname= document.getElementById("t1").value;
		var pwd= document.getElementById("t2").value;
		var letters = /^[A-Za-z]+$/;

		if(uname===''){
			alert('Please enter the user name.');
		}
		else if(pwd===''){
			alert('Please enter password.');
		}
		else if(letters.test(uname)){

			const url = 'http://localhost:8080/api/user/username?username="chiku"';
			const response = fetch(url);
			const data = response.json();
			this.setState({user:data});
			fetch('http://localhost:8080/api/user/username?username="chiku"',{method: "GET", headers: {'Content-Type': 'text/json'}})
				.then((res) =>{res.json()} )
			  	.then((result) => {
          			
          			console.log("Successful" + this.state.user);
				})
				.catch(console.log);
		}

		if(this.state.user.userName !== uname){
				alert('Please enter valid user name.');
			}
			else if(this.state.user.userPassword!==pwd){
				alert('Please enter valid password.');
			}
			else if((this.state.user.userName === uname) && (this.state.user.userPassword === pwd)){
				alert('Successfully Logged In');
				this.setState({
					isUserValid:true
				});
			}
	}

	render(){
		if(this.state.isUserValid){
			return(
				<HomeComponent user={this.state.user}/>
			);
		}
		else{
			return(
				<div className="main-container">
					<div className="login">
						<table cellSpacing="2" align="center" cellPadding="8" border="0">
						<tbody>
						<tr>
							<td align="right">Enter Username :</td>
							<td><input type="text" placeholder="Enter Username here" id="t1" className="tb" /></td>
						</tr>
						<tr>
							<td align="right">Enter Password :</td>
							<td><input type="password" placeholder="Enter Password here" id="t2" className="tb" /></td>
						</tr>
						<tr>
						<td></td>
						<td>
							<input type="submit" value="Login" className="login-btn" onClick={()=>this.login()} /></td>
						</tr>
						</tbody>
						</table>
					</div>
				</div>
			);
		}
		
	}
}

export default LoginContainer;
